  displayorder mediumint(8) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (classid),
  UNIQUE KEY classname (classname)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS uchome_eventfield;
