INSERT INTO uchome_blogfield VALUES('809','83272','a:3:{i:128;s:9:\"出版社\";i:129;s:6:\"北京\";i:130;s:9:\"金瓶梅\";}','<DIV>\r\n<DIV .=Section0 .=\"LAYOUT-GRID:  15.6pt none\">\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">商江扫描《金瓶梅》</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">已</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">由北京艺术与科学电子出版社</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">2011</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">年</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">3</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">月出版发行</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Verdana\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Verdana\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; TEXT-INDENT: 21pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; TEXT-INDENT: 21pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">本书作者对小说《金瓶梅》从若干方面进行梳理，以宋朝的社会大背景为依托，侧重从政治、经济、军事、法律、文化、社会、家庭等角度研究小说《金瓶梅》，去掉不适于大众传播的“自然主义的秽亵描写”的内容，使小说《金瓶梅》从所谓“淫书”的面具下解脱出来，以“教科书”的面貌展现于现代社会。此书比较适合高中以上文化程度的成年人阅读，有利于提高阅读者的人文素质。可以作为职业人普及《金瓶梅》等古典名著的培训教材。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Verdana\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; TEXT-INDENT: 21pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; TEXT-INDENT: 21pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">统一书号<FONT face=Arial>ISBN:</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Arial\'; mso-spacerun: \'yes\'\">978-7-900272-48-5.</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">定价</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">42元&nbsp;&nbsp;&nbsp;&nbsp;380千字</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; TEXT-INDENT: 21pt; PADDING-TOP: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">责任编辑：谢进&nbsp;孙丽娜&nbsp;&nbsp;&nbsp;封面设计：张晨</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"><BR></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"PADDING-RIGHT: 0pt; MARGIN-TOP: 0pt; PADDING-LEFT: 0pt; MARGIN-BOTTOM: 0pt; PADDING-BOTTOM: 0pt; PADDING-TOP: 0pt; TEXT-ALIGN: left\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\"></SPAN>