INSERT INTO uchome_blogfield VALUES('820','83272','','<DIV>\r\n<DIV .=Section0 .=\"LAYOUT-GRID:  15.6pt none\">\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 16pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">《李阳疯狂说英语》&nbsp;句型系列<FONT face=\"Times New Roman\">120</FONT><FONT face=宋体>个核心例句</FONT></SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 16pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">初步分类</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 16pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">LiYang&nbsp;&nbsp;Crazily&nbsp;&nbsp;Speak&nbsp;&nbsp;English</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 15pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">大庆广播电视大学&nbsp;商江&nbsp;&nbsp;&nbsp;学习笔记</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">提要：</SPAN><SPAN .=\"FONT-WEIGHT: normal; FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">李阳说，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">英语单词表示<FONT face=\"Times New Roman\">“</FONT><FONT face=宋体>疯狂</FONT><FONT face=\"Times New Roman\">”</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">至少</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">有四个说法</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">，但是，《李阳疯狂说英语》<FONT face=\"Times New Roman\">LiYang&nbsp;&nbsp;Crazily&nbsp;&nbsp;Speak&nbsp;&nbsp;English&nbsp;</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">：</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">crazy&nbsp;&nbsp;<FONT face=宋体>疯狂的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>蠢的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>古怪的&nbsp;&nbsp;疯狂的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>蠢的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>古怪的</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;美音&nbsp;<FONT face=\"Times New Roman\">[\'krezi]</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">insane&nbsp;&nbsp;(<FONT face=宋体>患</FONT><FONT face=\"Times New Roman\">)</FONT><FONT face=宋体>精神病的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>精神错乱的</FONT><FONT face=\"Times New Roman\">;</FONT><FONT face=宋体>疯狂的&nbsp;英音：</FONT><FONT face=\"Times New Roman\">[in\'sein]</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">mad&nbsp;&nbsp;<FONT face=宋体>疯狂的</FONT><FONT face=\"Times New Roman\">,</FONT><FONT face=宋体>狂热的&nbsp;英音：</FONT><FONT face=\"Times New Roman\">[mæd]&nbsp;</FONT><FONT face=宋体>美音：</FONT><FONT face=\"Times New Roman\">[mæd]</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">madness&nbsp;&nbsp;<FONT face=宋体>疯狂</FONT><FONT face=\"Times New Roman\">,</FONT><FONT face=宋体>神经错乱&nbsp;英音：</FONT><FONT face=\"Times New Roman\">[\'mædnis]</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">go&nbsp;bananas&nbsp;&nbsp;<FONT face=宋体>发疯的</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'宋体\'; mso-spacerun: \'yes\'\">&nbsp;美音&nbsp;<FONT face=\"Times New Roman\">[</FONT><FONT face=宋体>ɡ</FONT><FONT face=\"Times New Roman\">o&nbsp;bə\'nænəz]</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">关键词：李阳疯狂说英语；&nbsp;句型系列；&nbsp;核心例句；</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">120</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">个；</SPAN><SPAN .=\"FONT-WEIGHT: bold; FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">2011</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">7</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">8</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">文汇报<FONT face=\"Times New Roman\">(</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">记者&nbsp;李雪林</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">)</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">《</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">复旦教授成<FONT face=\"Times New Roman\">“</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">外语达人</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">”&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">掌握语言横贯欧亚大陆》：</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">白钢<FONT face=\"Times New Roman\">1995</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年考入上海财经大学金融系，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">1999</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年本科毕业后，白钢如愿前往德国特里尔大学留学。在德国拿了博士学位后，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">2009</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年开始任教于复旦大学中文系比较文学专业。这位</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">34</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">岁的副教授掌握的语言种类全写下来需要半页纸，除了西方现代主要语种</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">——</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">英语、法语、德语、俄语、意大利语、西班牙语、希腊语之外，他更着力研究和掌握了世界各大文明体的几十种古典语言，比如赫提语、古希腊语、拉丁语、吠陀语，梵文、巴利文、阿维斯特语、古波斯语、古亚美尼亚语、哥特语、古爱尔兰语、古教会斯拉夫语、立陶宛语、吐火罗语、阿卡德语、圣经希伯莱语、阿拉美语、腓尼基语、乌加里特语</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">……</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">有人说，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">“</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">欧洲的古典语言他都会，他掌握的语言帝国疆域横贯整个欧亚大陆。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">”</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">在白钢看来，学习十种以下的语言，不需要任何天赋，最关键是要持之以恒，付出极大的热情和精力，这才能克服记忆的难关。另外要学习一些比较语言学方法，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">“</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">学过某种语言，在此基础上再学一种语言，一定是触类旁通的</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">”</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;&nbsp;学英语之前应解决思想认识问题。为什么要学习英语？</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">(</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">学习英语有什么用处</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">?</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">有的人一个英语单词也不会，也生活一辈子；有些人一句英语也没说过，一辈子生活也不错。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">)</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">哪些人需要学习英语？（英语翻译和英语教师碧溪学英语，别人未必需要）英语好学吗？（谁都可以学习英语吗？没有英语环境，学了英语用不上）怎么学习英语？（学习英语需要技巧吗？死记硬背靠不住）学习英语需要多少时间、精力、经费？（学习英语有好处，不学英语有坏处吗？）英语水平多高可以<FONT face=\"Times New Roman\">“</FONT><FONT face=宋体>狂</FONT><FONT face=\"Times New Roman\">”</FONT><FONT face=宋体>？英语水平多高可以</FONT><FONT face=\"Times New Roman\">“</FONT><FONT face=宋体>疯狂</FONT><FONT face=\"Times New Roman\">”</FONT><FONT face=宋体>？</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;&nbsp;2008<FONT face=宋体>年</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">4</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">13</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日&nbsp;，环球时报（管理咨询师　刘云）《全民学英语是个赔本买卖》：中国有</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">4</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">亿多英语学习者</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">(1</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">亿成人、</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">3</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">亿学生</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">)</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">，很少有人考虑有没有必要让这么多人去学英语？依笔者之见，这其中至少应该有</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">90%</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">的人不学英语。&nbsp;对全国４亿学英语的人来说，以英语为职业或在工作中需经常用到英语的人最多占当年毕业生总数的</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">10%</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">，剩下</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">90%</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">的学生在今后几十年的工作、生活中很难用上英语，但为了这</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">10%</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">的学生，却强迫</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">90%</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">的学生也把大量宝贵时间耗费在攻读英语上，这既是对学生智力的无用消耗，也是对教育资源的巨大浪费。从社会分工来说是不必要的，从经济上来讲绝对是赔本的买卖。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;2011<FONT face=宋体>年</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">3</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">5</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日，文汇报《学外语，可防老年痴呆？》：【《国家地理》杂志</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">2</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">18</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日报道】最新研究显示，会说两门以上的语言，可以延缓大脑衰老过程中的痴呆。科学家早就发现，在完成大脑执行控制系统发出的指令时，会说双语的人比单一语言者表现更好。最近，加拿大多伦约克大学心理学家多爱伦<FONT face=\"Times New Roman\">·</FONT><FONT face=宋体>比亚韦斯托克又发现，双语优势将持续到老年，即使彼时的大脑敏捷度已经自然下降。根据比亚韦斯托克新近发表在《神经学》上的研究成果，双语者比单语者被诊断出患有老年痴呆症平均约晚</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">4</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">2011</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">6</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">6</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日，</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">北京晚报《</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">高考故事：俞敏洪<FONT face=\"Times New Roman\">——</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">英语</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">33</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分的落榜考生》：</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">新东方的创始人俞敏洪都曾经历过两次落榜。俞敏洪参加了<FONT face=\"Times New Roman\">1978</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年的高考。当时的录取分数其实很低，他报考的常熟市地区师专外语录取分数线是</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">38</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分，俞敏洪的英语却只考了</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">33</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">1979</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年的高考又开始了，俞敏洪再度进入考场。这一年，俞敏洪的总分过了录取分数线，但英语只考了</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">55</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分，而常熟师专的录取分数线变成了</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">60</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分，结果再度落榜。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">1980</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">年的高考开始了，英语考试时间是两个小时，俞敏洪仅仅用了</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">40</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分钟就交了卷。分数出来以后，俞敏洪的英语是</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">95</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分，总分</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">387</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分。当年，北大的录取分数线是</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">380</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">分。俞敏洪拿到通知书一看是北京大学，当时就乐疯了，和两个考上大学的同学一起，像范进中举一样跑到马路中间又蹦又跳，连大卡车都停下来了。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">&nbsp;2011<FONT face=宋体>年</FONT></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">5</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">23</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日，中国新闻网《翻译协会称中国翻译人才缺口大&nbsp;师资严重匮乏》：翻译教育发展国际学术研讨会</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">5</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">月</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">21</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日至</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">22</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">日在京举行。中国翻译协会称，目前中国翻译人才缺口大，师资严重匮乏。</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">The&nbsp;right&nbsp;rat&nbsp;had&nbsp;a&nbsp;sad&nbsp;side!</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\"></SPAN></P>\r\n<P .=p0 .=\"MARGIN-TOP: 0pt; MARGIN-BOTTOM: 0pt\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">百科名片介绍：</SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">李阳，</SPAN><SPAN><a href=\"http://home.tripbaba.com/link.php?url=http://baike.baidu.com/view/8883.htm\" target=\"_blank\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">疯狂英语</SPAN></A></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">创始人，全球著名英语口语教育专家，英语成功学励志导师，中国教育</SPAN><SPAN><a href=\"http://home.tripbaba.com/link.php?url=http://baike.baidu.com/view/403182.htm\" target=\"_blank\"><SPAN .=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-spacerun: \'yes\'\">慈善家</SPAN></A></SPAN><SPAN .=\"FONT-SIZE: 10.5pt; FONT-F